{"SBI":{"createBody": {
  "externalReference":[{
    "externalReferenceType":"CompositeService",
    "name":"Ericsson"   
  }],
  "relatedParty":[{
    "role":"Customer",
    "id":"composite",
    "@referredType":"composite"   
  }],
  "serviceOrderItem":[{
      "action":"add",

      "service":{
          "state":"active",
          "description":"Cloud Access Service",
          "serviceSpecification":{
            "href":"Cloud Access Service",
            "name":"Cloud Access Service",
            "version":"v1.0"   
          },
         "relatedParty":[{         
            "id":"composite",
            "role":"Customer"
        }],
          "serviceCharacteristic":
             [{"name":"latency","value":"10"},{"name":"Bandwidth","value":"11"},{"name":"Availability","value":"12"},{"name":"NumberOfDevices","value":"1"},{"name":"Location","value":"30855"}]   


        }

  }]
},
"deleteBody":{
  "serviceOrder": {
    "id": "__fromResponse(orderId)"
  }
},
"SBIproperties":{
   "systemName": "NaaS",
   "polling_interval": 2,
   "max_polling_retries": 10,
   "timeout": 6000,   
   "methods":{
      "create": { 
        "method":"POST",
        "url": "__fromSystem(URL)/serviceOrdering/v4/serviceOrder/cloud",
        "body": "__fromJSON(createBody)",
        "response_parser": {
         "orderId": ".id",
         "serviceId": ".serviceOrderItem[0].service.id",
         "state": ".serviceOrderItem[0].state"
        },
        "response_ok": "checkResponses('__fromResponse(state)',['completed','Acknowledged','acknowledged'])",
        "response_fail": "checkResponses('__fromResponse(state)',['Failed'])",
        "outputs": {
         "orderId": "orderId",
         "serviceId": "serviceId"
        }
      },
      "delete":{
        "method":"POST",
        "url":  "__fromSystem(URL)/serviceOrdering/v4/cancelServiceOrder/cloud",
        "body": "__fromJSON(deleteBody)",
        "response_parser": {
         "orderId": ".id",
         "serviceId": ".serviceOrder.id",
         "state": ".state"
        },
        "response_ok": "checkResponses('__fromResponse(state)',['Completed','accepted'])",
        "response_fail": "checkResponses('__fromResponse(state)',['Failed'])"		
      },
      "pooling_delete":{
        "method":"GET",
        "url":  "__fromSystem(URL)/serviceOrdering/v4/serviceOrder/cloud/__fromResponse(serviceId)",
        "response_parser": {
         "orderId": ".id",
         "serviceId": ".serviceOrderItem[0].service.id",
         "state": ".serviceOrderItem[0].state"
        },
        "response_ok": "checkResponses('__fromResponse(state)',['Completed'])",
        "response_fail": "checkResponses('__fromResponse(state)',['Failed'])"
      }	  
  },
  "headers":{
    "X-Correlation-ID":  "__randomUUID()",
    "X-Latest-Version":"v4.1.0",
    "X-Service-Version":"v3.0.0",
    "X-Group-ID":"ePilot",
    "Authorization": "Bearer __fromSystem(access_token)",
    "Content-Type":"application/json",
    "Accept":"*/*"
    }
}
}
}
